package chap03;

public class AsssigmentOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0 ;
		result += 10 ;
		System.out.println("result="+result);	//result에 10
		result -= 5 ;
		System.out.println("result="+result);	//result에 5
		result *= 3 ;
		System.out.println("result="+result);	//result에 15
		result /= 5 ;
		System.out.println("result="+result);	//result에 3
		result %= 3 ;
		System.out.println("result="+result);	//result에 0 대입
	}

}
